import groovy.json.*

def ff=session.get()
def json=""
if(!ff)return

ff.write{rawIn,rawOut->
    //parse json from input stream of the flow file
    json = rawIn.withReader("UTF-8"){reader-> 
        new JsonSlurper().parse( reader )
    }	

    //write json to flow file output stream
    rawOut.withWriter("UTF-8"){writer->
        new JsonBuilder(json).writeTo(writer)
    }


}
   if(json.flow2_attributes.toInteger()<10){
session.transfer(ff, REL_FAILURE)
   }else{
session.transfer(ff, REL_SUCCESS)
    }

import groovy.json.*

def ff=session.get()
def json=""
if(!ff)return

ff.write{rawIn,rawOut->
    //parse json from input stream of the flow file
    json = rawIn.withReader("UTF-8"){reader-> 
        new JsonSlurper().parse( reader )
    }	
    json."new_field_for_name" = "adem"
    //write json to flow file output stream
    rawOut.withWriter("UTF-8"){writer->
        new JsonBuilder(json).writeTo(writer)
    }


}
session.transfer(ff, REL_SUCCESS)
